function updateBonus(){document.getElementsByClassName("bonus-counter")[0].innerHTML=bonus}function plusBonus(){bonus++,updateBonus()}function undoBonus(){0!=bonus&&bonus--,updateBonus()}function resetClock(){bonus=0,updateBonus()}function updateClock(){currentTime=new Date,seconds=6*currentTime.getSeconds(),minutes=6*currentTime.getMinutes()+6*bonus,hours=30*currentTime.getHours()+(currentTime.getMinutes()+bonus)/2,secondHand.style.WebkitTransform="rotate("+seconds+"deg)",secondHand.style.transform="rotate("+seconds+"deg)",minuteHand.style.WebkitTransform="rotate("+minutes+"deg)",minuteHand.style.transform="rotate("+minutes+"deg)",hourHand.style.WebkitTransform="rotate("+hours+"deg)",hourHand.style.transform="rotate("+hours+"deg)"}var clock=document.getElementsByClassName("clock")[0],secondHand=document.getElementsByClassName("seconds")[0],minuteHand=document.getElementsByClassName("minutes")[0],hourHand=document.getElementsByClassName("hours")[0],bonusButton=document.getElementsByClassName("bonus")[0],undoButton=document.getElementsByClassName("undo")[0],resetButton=document.getElementsByClassName("reset")[0],seconds=0,minutes=0,hours=0,bonus=0;bonusButton.addEventListener("click",plusBonus,!1),undoButton.addEventListener("click",undoBonus,!1),resetButton.addEventListener("click",resetClock,!1),clock.addEventListener("click",plusBonus,!1);var startClock=setInterval(function(){updateClock()},1e3);